import requests

def get_platrofrms_and_instruments():
    platforms_url = "https://gcmdservices.gsfc.nasa.gov/kms/concepts/concept_scheme/platforms?format=json"
    pltf_json = requests.get(platforms_url).json()
    platform_id = {pltf_json['concepts'][i]['prefLabel']:pltf_json['concepts'][i]['id'] for i in range(len(pltf_json['concepts']))}
    platforms_names = list(platform_id.keys())

    instruments_url = "https://gcmdservices.gsfc.nasa.gov/kms/concepts/concept_scheme/instruments?format=json"
    instr_json = requests.get(instruments_url).json()
    instrument_id = {instr_json['concepts'][i]['prefLabel']:instr_json['concepts'][i]['id'] for i in range(len(instr_json['concepts']))}
    instruments_names = list(instrument_id.keys())

    print(f'Platforms: {platforms_names}')
    print(f'Instruments: {instruments_names}')
    return platforms_names, instruments_names

